using System;
using System.Collections.Generic;
using System.IO;
using System.Linq;
using System.Text;

namespace GreentegCoreApp1
{
    static class Variables
    {
        public static bool auto_panic = debug_mode;
        public static int from_temp = 20;
        public static int to_temp = 42;
        public static int max_temp_val = 254;
        public static string FARENHEIT_KEY = "Farenheit";
        public static bool record_mode = false;
        public static string record_mode_core_name = "core [RecM]";
        public static bool ServerMode = false;
        public static string ServerIp = "0.0.0.0";
        public static string URL = "http://" + ServerIp + "/req";
        public static string BASEPcLinkURL = "http://pclink.gearhostpreview.com";
        public static bool debug_mode = true;
        public static bool design_mode = true;
        public static bool inverse_temp = false;


        public static int min_temp_val = 0;
        public static string stickman = "data:image/jpeg;base64,";
        public static double compute_temp(byte input) 
        {
            float offset = (to_temp - from_temp) / max_temp_val;
            return offset * input;
        }
        public static float compute_temp2p(byte input) { return MathF.Round((float)compute_temp(input), 2); }
        public static string serviceuuid_start = "00001809";
        public static string characteristicuuid_start = "00002a1c";
        public static Stream stickman_func()
        {
            return ToImage.base64toimagestream(Variables.stickman);
        }
    }
    static class ToImage
    {
        public static Stream base64toimagestream(string base64)
        {
            byte[] imageBytes = Convert.FromBase64String(base64);
            // Convert byte[] to Image
            using (var ms = new MemoryStream(imageBytes, 0, imageBytes.Length))
            {
                return ms;
            }
            return null;
        }
    }
    static class Bytepacker
    {
        public static string pack(byte[] bt)
        {
            string o = "";
            foreach(byte b in bt)
            {
                o = o + b.ToString() + "-";
            }
            return o;
        }
        public static long packlong(byte[] bt)
        {
            long o = 0;
            foreach (byte b in bt)
            {
                o = o + b;
            }
            return o;
        }
    }
}
